import React, { useState } from 'react';
import classes from './SelectOption.module.scss';

const SelectOption = (props) => {
    const [values, setValues] = useState(props.values);
    const [placeholder, setPlaceholder] = useState(props.placeholder);
    //
    let buttons = [];
    if(props.type === 'dynamic')
        buttons = 
        [
            <i class="fa fa-minus-square" onClick={(e) => DeleteSelectOption(e)} aria-hidden="true"></i>,
            <i class="fa fa-plus-square" onClick={AddSelectOption()} aria-hidden="true"></i>
        ];
    //
    const [selections, setSelections] = useState(
    [
        <div className={classes.dynamic_option}>
            {buttons[0]}
            <select className={props.className}>
                {InsertValues()}
            </select>
            {buttons[1]}
        </div>
    ]);

    function InsertValues(new_placeholder) {
        const elements = [];
        if(props.placeholder)
            elements.push(<option selected hidden>{new_placeholder ? new_placeholder : placeholder}</option>);
        if(values && values.length != 0)
        {
            for(let i = 0; i < values.length; i++)
            {
                elements.push(<option>{values[i]}</option>);
            }
        }
        return elements;
    }

    function DeleteSelectOption (e) {
        console.log('delete');
        if(selections.length > 1)
        {
            console.log(e.currentTarget);
        }
    }

    function AddSelectOption() {
        console.log('add');
        
        selections.push(
        <div className={classes.dynamic_option}>
            {buttons[0]}
            <select className={props.className}>
                {InsertValues()}
            </select>
            {buttons[1]}
        </div>);

        setSelections(selections);
    }

    return (
        <div className={classes.select_option}>
            {selections}
        </div>
    )
};

export default SelectOption;
