import React, { useState } from 'react';
import classes from './ListItem.module.css';

const ListItem = (props) => {
    const[services, setServices] = useState(props.services);
    const[imgSrc, serImage] = useState(props.imgSrc);
    
    function ConvertServices(){
        let elements = [];
        if(services && services.length != 0)
        {
            for(let i = 0; i < services.length; i++)
            {
                let service = services[i];
                // This is just example, services will be another
                switch(service)
                {
                    case 'food':
                        service = <i class="fa fa-cutlery" aria-hidden="true"></i>;
                        break;
                    case 'bath':
                        service = <i class="fa fa-bath" aria-hidden="true"></i>;
                        break;
                    default:
                        console.log(service + ' service don`t support');
                        continue;
                }
                elements.push(service);
            }
        }
        debugger
    }

    return (
        <div className={classes.list_item + ' ' + classes.className}
            style={{backgroundImage: `url(${props.imgSrc})`}}>
            <div className={classes.top}>
                <h1 className={classes.title}>{props.title}</h1>
            </div>
            <div className={classes.bottom}>
                <div className={classes.address}>
                    <i class="fa fa-map-marker" aria-hidden="true"></i>
                    <p>
                        {props.address}
                    </p>
                </div>
                <div className={classes.price}>
                    <i class="fa fa-money" aria-hidden="true"></i>
                    <p>
                        {props.price}
                    </p>
                </div>
                <div className={classes.services}>
                    {/* IMAGE SERVICES */}
                    {ConvertServices()}
                </div>
            </div>
        </div>
    )
}

export default ListItem;
