import React, { useState } from 'react';
import FormBooking from './../CustomElements/FormBooking.jsx';
import Slider from './../CustomElements/Slider.jsx';
import classes from './Resort.module.css';

const Resort = (props) => {
    // const[idItem, setId] = useState(props.idItem);
    // По идее здесь должен идти запрос к бд, а не передача через пропсы
    // Но это временно для проверки пока бд не прикручена

    // Нужно заменить на конст
    // const[id, setId] = useState(props.id);
    // const[images, setImages] = useState(props.images);
    // const[title, setTitle] = useState(props.title);
    // const[price, setPrice] = useState(props.price);
    let[id, setId] = useState(props.id);
    let[images, setImages] = useState(props.images);
    let[title, setTitle] = useState(props.title);
    const[text, setText] = useState(props.text);
    const[address, setAddress] = useState(props.address);
    let[price, setPrice] = useState(props.price);
    const[services, setServices] = useState(props.services);

    function GetResort() {
        let id_new = props.match.params.id;
        // Дальше по этому айди запрос к бд который возврщает пансионат/санаторий
        id = id_new;
        images = 
        [ 
            'https://geekster.ru/wp-content/uploads/2017/09/warhammer-40k.jpg', 
            'https://s1.1zoom.ru/big3/72/419476-Kycb.jpg',
            'https://altwall.net/img/deathgroup/03_1024.jpg'
        ];
        title = 'Mr ya Resort & SPA 5*';
        price = '20 000 руб.';
        console.log(id + ' ID resort');
    }

    function ExpandDescription() {
        
    }

    GetResort();

    return (
        <div className={classes.resort}>

            <div className={classes.enter}>
                <Slider className={classes.slider} images={images}/>
                <div className={classes.info}>
                    <h1>{title}</h1>
                    {/* STARS */}
                    <div>
                        <h2>от {price}</h2>
                        <span>за человека</span>
                    </div>
                    <button>Забронировать</button>
                </div>
                <div className={classes.wallet}>
                    <i class="fa fa-credit-card" aria-hidden="true"></i>
                    <p>
                        Можно оплатить наличными или картой на месте
                    </p>
                </div>
            </div>

            <div className={classes.description}>
                <div>
                    <div className={classes.back}/>
                    <h1>Описание</h1>
                    <p>
                        {text}Идейные соображения высшего порядка, а также укрепление и развитие структуры играет важную роль в формировании существенных финансовых и административных условий. Разнообразный и богатый опыт консультация с широким активом обеспечивает широкому кругу. Идейные соображения высшего порядка, а также дальнейшее развитие различных форм деятельности позволяет оценить значение новых предложений.
    Равным образом постоянный количественный рост и сфера нашей активности играет важную роль в формировании системы обучения кадров, соответствует насущным потребностям. Товарищи! сложившаяся структура организации представляет собой интересный эксперимент проверки направлений прогрессивного развития.
    С другой стороны постоянное информационно-пропагандистское обеспечение нашей деятельности обеспечивает широкому кругу (специалистов) участие в формировании позиций, занимаемых участниками в отношении поставленных задач. Значимость этих проблем настолько очевидна, что дальнейшее развитие различных форм деятельности обеспечивает широкому кругу (специалистов) участие в формировании новых предложений.
                    </p>
                </div>
                <i class="fa fa-arrow-down" aria-hidden="true"></i>
            </div>

            <div className={classes.services}>
                <div className={classes.available}>

                </div>
                <div className={classes.common}>

                </div>
                <div className={classes.rooms}>

                </div>
            </div>

            <div className={classes.map}>

            </div>

            <FormBooking className={classes.booking}/>
        </div>
    )
}

export default Resort;
