import React, { useState } from 'react';
import classes from './Slider.module.css';

const Slider = (props) => {
    //images is massive, full of src like: http://mlp, ./images/image/jpg
    const[images, setImages] = useState(props.images);
    const[index, setIndex] = useState(props.index ? props.index : 0);
    const[previos, setPrevios] = useState(props.previos ? props.previos : 1); 

    function AddImages() {
        let elements = [];
        let className = index > previos ? classes.to_right : classes.to_left;
        // if()
        // {
        //     //to right
        //     className = ;
        // }
        // else
        // {
        //     //to left

        // }
        if(images && images.length != 0)
        {
            for(let i = 0;i < images.length; i++)
            {
                if(i === index)
                {
                    elements.push(<img className={classes.item + ' ' + className} src={images[i]}></img>)
                }
                else if(i === previos)
                {

                }
                else
                    elements.push(<img className={classes.item} src={images[i]}></img>)
            }
        }
        return elements;
    }

    function OnClick(e) {
        if(e.target.classList.contains(classes.right))
        {
            //right
            if(index < images.length - 1)
            {
                setPrevios(index - 1);
                setIndex(index + 1);
            }
        }
        else
        {
            //left
            if(index > 0)
            {
                setPrevios(index + 1);
                setIndex(index - 1);
            }
        }
        console.log('Now: ' + index + '\n' + 'Previos: ' + previos);
    }

    return (
        <div className={classes.slider}>
            <div className={classes.left} onClick={(e) => OnClick(e)}>
                <i class="fa fa-angle-left" aria-hidden="true"></i>
            </div>
            {AddImages()}
            <div className={classes.right} onClick={(e) => OnClick(e)}>
                <i class="fa fa-angle-right" aria-hidden="true"></i>
            </div>
        </div>
    )
}

export default Slider;
