import React, { useState } from 'react';
import classes from './SelectOption.module.scss';

const SelectOption = (props) => {
    const [values, setValues] = useState(props.values);
    const [placeholder, setPlaceholder] = useState(props.placeholder);
    //
    const [selections, setSelections] = useState([]);

    function InsertValues() {
        const elements = [];
        if(props.placeholder)
            elements.push(<option selected hidden>{placeholder}</option>);
        if(values && values.length != 0)
        {
            for(let i = 0; i < values.length; i++)
            {
                elements.push(<option>{values[i]}</option>);
            }
        }
        return elements;
    }

    function InsertSelections() {
        const elements = [];
        if(selections);
    }

    function AddSelect() {
        let buttons = [];
        if(props.type === 'dynamic')
            buttons = 
            [
                <i class="fa fa-minus-square" onClick={AddSelect()} aria-hidden="true"></i>,
                <i class="fa fa-plus-square" onClick={AddSelect()} aria-hidden="true"></i>
            ];
        
        selections.push(
        <div className={classes.dynamic_option}>
            {buttons[0]}
            <select className={props.className}>
                {InsertValues()}
            </select>
            {buttons[1]}
        </div>);

        setSelections(selections);
    }

    return (
        <div className={classes.select_option}>
            {InsertSelections()}
            {/*  */}
            <div className={classes.dynamic_option}>
                {props.type === 'dynamic' ? (<i class="fa fa-minus-square" onClick={AddSelect()} aria-hidden="true"></i>) : ''}
                <select className={props.className}>
                    {InsertValues()}
                </select>
                {props.type === 'dynamic' ? (<i class="fa fa-plus-square" onClick={AddSelect()} aria-hidden="true"></i>) : ''}
            </div>
            {/*  */}
        </div>
    )
};

export default SelectOption;
